import React from "react";

class Form extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            nome: {
                value: "",
            },
            idade: {
                value: "",
            },
            genero: {
                value: "",
            }
        }
    }

    mostrarTipoDocumento() {
        const estadoCivil = document.getElementById('estadoCivil').value;
        const tipoDocumentoContainer = document.getElementById('tipoDocumentoContainer');
  
        if (estadoCivil === 'casado') {
          tipoDocumentoContainer.style.display = 'none';
        } else {
          tipoDocumentoContainer.style.display = 'block';
        }
      }

    render() {
        return(
            <div>
                <form>
                    <label>Nome:</label>
                    <input type="text" name="nome" />

                    <label>Idade:</label>
                    <input type="number" name="idade" />

                    <label>Genero:</label>
                    <input type="text" name="genero" />

                    <label for="estadoCivil">Estado Civil:</label>
                    <select id="estadoCivil" onchange="mostrarTipoDocumento()">
                        <option value="solteiro">Solteiro(a)</option>
                        <option value="casado">Casado(a)</option>
                        <option value="divorciado">Divorciado(a)</option>
                        <option value="viuvo">Vi√∫vo(a)</option>                                                                 
                    </select>


                    <div id="tipoDocumentoContainer">
                    <label for="tipoDocumento">Tipo de Documento:</label>
                    <select id="tipoDocumento">
                        <option value="rg">RG</option>
                        <option value="cpf">CPF</option>
                        <option value="passaporte">Passaporte</option>
                    </select>
                    </div>

                </form>
            </div>
        )
    }
}

export default Form;
