import React from "react";

class Form extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      nome: {
        value: "",
      },
      idade: {
        value: "",
      },
      genero: "", // Armazena apenas o valor do gênero selecionado
      estadoCivil: {
        value: "",
      },
      tipoDocumento: {
        value: "rg", // valor padrão para o tipo de documento
      },
      loadingVisible: false,
      formularioEnviado: false,
    };
  }

  handleChange = (event) => {
    const { name, value } = event.target;
    this.setState({
      [name]: {
        value,
      },
    });
  };

  handleChangeGender = (event) => {
    const { name, value } = event.target;
    this.setState({
      [name]: value, // Atualiza o valor do gênero selecionado
    });
  };

  handleSubmit = (event) => {
    event.preventDefault();

    // acessar os valores dos campos no estado do componente
    const { nome, idade, genero, estadoCivil, tipoDocumento } = this.state;

    console.log("Nome:", nome.value);
    console.log("Idade:", idade.value);
    console.log("Gênero:", genero);
    console.log("Estado Civil:", estadoCivil.value);
    console.log("Tipo de Documento:", tipoDocumento.value);

    this.setState({ loadingVisible: true });
    setTimeout(() => {
      this.setState({
        loadingVisible: false,
        formularioEnviado: true,
        // Reseta os campos após o envio
        nome: { value: "" },
        idade: { value: "" },
        genero: "",
        estadoCivil: { value: "" },
        tipoDocumento: { value: "rg" },
      });
    }, 2000);
  };

  render() {
    return (
      <div className="main">
        <form onSubmit={this.handleSubmit}>
          <label>Nome:</label>
          <input
            type="text"
            value={this.state.nome.value}
            name="nome"
            onChange={this.handleChange}
          />

          <label>Idade:</label>
          <input
            type="number"
            value={this.state.idade.value}
            name="idade"
            onChange={this.handleChange}
          />

          <label>Genero:</label>
          <div>
            <input
              type="radio"
              name="genero"
              value="Masculino"
              id="masculino"
              checked={this.state.genero === "Masculino"}
              onChange={this.handleChangeGender}
            />
            <label>Masculino</label>
          </div>

          <div>
            <input
              type="radio"
              name="genero"
              value="feminino"
              id="feminino"
              checked={this.state.genero === "feminino"}
              onChange={this.handleChangeGender}
            />
            <label>Feminino</label>
          </div>

          <div>
            <input
              type="radio"
              name="genero"
              value="outro"
              id="outro"
              checked={this.state.genero === "outro"}
              onChange={this.handleChangeGender}
            />
            <label>Outro</label>
          </div>

          <label htmlFor="estadoCivil">Estado Civil:</label>

          <select
            name="estadoCivil"
            value={this.state.estadoCivil.value}
            id="estadoCivil"
            onChange={this.handleChange}
          >
            <option value="solteiro">Solteiro(a)</option>
            <option value="casado">Casado(a)</option>
            <option value="divorciado">Divorciado(a)</option>
            <option value="viuvo">Viúvo(a)</option>
          </select>

          <div>
            <label htmlFor="tipoDocumento">Tipo de Documento:</label>

            <select
              value={this.state.tipoDocumento.value}
              onChange={this.handleChange}
              name="tipoDocumento"
              id="tipoDocumento"
            >
              <option value="rg">RG</option>
              <option value="cpf">CPF</option>
              <option value="passaporte">CNPJ</option>
            </select>
          </div>

          <button type="submit">Enviar</button>
        </form>

        {this.state.loadingVisible && <div className="loading"></div>}

        {this.state.formularioEnviado && !this.state.loadingVisible && (
          <table>
            <thead>
              <tr>
                <th>Nome</th>
                <th>Idade</th>
                <th>Genero</th>
                <th>Estado civil</th>
                <th>Documento</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>{this.state.nome.value}</td>
                <td>{this.state.idade.value}</td>
                <td>{this.state.genero}</td>
                <td>{this.state.estadoCivil.value}</td>
                <td>{this.state.tipoDocumento.value}</td>
              </tr>
            </tbody>
          </table>
        )}
      </div>
    );
  }
}

export default Form;
