import React from "react";

class Form extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            nome: {
                value: "",
            },
            idade: {
                value: "",
            },
            genero: {
                value: "",
            },
            estadoCivil: {
                value: "",
            },
            tipoDocumento: {
                value: "rg", // valor padrão para o tipo de documento
            },
            loadingVisible: false,

        }
    }


    handleChange = (event) => {
        const { name, value } = event.target;
        this.setState({
            [name]: {
                value,
            },
        });
    };

    handleChangeGender = (event) => {
        const { name, value } = event.target;
        this.setState({
            genero: {
                value,
            },
        });
    };
    


    handleSubmit = (event) => {
        event.preventDefault();
    
        // acessar os valores dos campos no estado do componente
        const { nome, idade, genero, estadoCivil, tipoDocumento } = this.state;
    
        console.log("Nome:", nome.value);
        console.log("Idade:", idade.value);
        console.log("Gênero:", genero.value);
        console.log("Estado Civil:", estadoCivil.value);
        console.log("Tipo de Documento:", tipoDocumento.value);

        this.setState({ loadingVisible: true });
    setTimeout(() => {
        this.setState({ loadingVisible: false });
    }, 4000);


    };
    
    

    render() {
        return(
            <div>
                <form onSubmit={this.handleSubmit}>
                    <label>Nome:</label>
                    <input type="text" value={this.state.nome.value} name="nome" 
                    onChange={this.handleChange}/>

                    <label>Idade:</label>
                    <input type="number" value={this.state.idade.value} name="idade" 
                    onChange={this.handleChange}/>

                    <label>Genero:</label>   
                    
                        <div>
                            <input 
                            type="radio" 
                            name="Masculino"
                            value="Masculino" 
                            id="masculino" 
                            onChange={this.handleChangeGender}
                            />
                            <label>Masculino</label>
                        </div> 

                        <div>
                            <input 
                            type="radio" 
                            name="feminino" 
                            value="feminino" 
                            id="feminino"
                            onChange={this.handleChangeGender}

                            />
                            <label>Feminino</label>
                        </div> 

                        <div>
                            <input 
                            type="radio" 
                            name="outro" 
                            value="outro" 
                            id="outro"
                            onChange={this.handleChangeGender}

                            />
                            <label>Outro</label>
                        </div> 


                    <label htmlFor="estadoCivil">Estado Civil:</label>

                    <select 
                    name="estadoCivil"
                    value={this.state.estadoCivil.value}
                    id="estadoCivil" 
                    onChange={this.handleChange}
                    >


                        <option value="solteiro">Solteiro(a)</option>
                        <option value="casado">Casado(a)</option>
                        <option value="divorciado">Divorciado(a)</option>
                        <option value="viuvo">Viúvo(a)</option>  


                    </select>


                    <div>
                        
                        <label htmlFor="tipoDocumento">Tipo de Documento:</label>
                        
                        <select
                        value={this.state.tipoDocumento.value}
                        onChange={this.handleChange}
                        name="tipoDocumento" 
                        id="tipoDocumento">
                            <option value="rg">RG</option>
                            <option value="cpf">CPF</option>
                            <option value="passaporte">CNPJ</option>
                        </select>

                    </div>

                    <button type="submit">Enviar</button>

                </form>

                {this.state.loadingVisible && <div className="loading"></div>}

                {this.state.loadingVisible && 
                <table>
                <thead>
                    <tr> /* Linha do cabeçalho da tabela */
                        <th>Cabeçalho da Coluna 1</th>
                        <th>Cabeçalho da Coluna 2</th>
                        /* Outros cabeçalhos de coluna, se houver */
                    </tr>
                </thead>
                <tbody> /* Corpo da tabela (opcional) */
                    <tr> /* Linha de dados da tabela */
                        <td>Dado da Coluna 1</td>
                        <td>Dado da Coluna 2</td>
                        /* Outros dados de coluna, se houver */
                    </tr>
                    /* Outras linhas de dados, se houver */
                </tbody>
            </table>  }

            </div>
        )
    }
}

export default Form;
