import React from "react";

class Form extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            nome: {
                value: "",
            },
            idade: {
                value: "",
            },
            genero: {
                value: "",
            },
            estadoCivil: {
                value: "",
            }
        }
    }

    mostrarTipoDocumento() {
        const estadoCivil = document.getElementById('estadoCivil').value;
        const tipoDocumentoContainer = document.getElementById('tipoDocumentoContainer');

        if (estadoCivil === 'casado') {
            tipoDocumentoContainer.style.display = 'none';
        } else {
            tipoDocumentoContainer.style.display = 'block';
        }
    }

    handleSubmit = (event) => {

        event.preventDefault();

        const {nome, idade, genero, estadoCivil } = this.state;
    }

    render() {
        return(
            <div>
                <form onSubmit={this.handleSubmit}>
                    <label>Nome:</label>
                    <input type="text" value={this.state.nome.value} name="nome" 
                    onChange={this.handleChange}/>

                    <label>Idade:</label>
                    <input type="number" value={this.state.idade.value} name="idade" 
                    onChange={this.handleChange}/>

                    <label>Genero:</label>
                    <input type="text" value={this.state.genero.value} name="genero" 
                    onChange={this.handleChange}/>

                    <label htmlFor="estadoCivil">Estado Civil:</label>

                    <select 
                    name="estadoCivil"
                    value={this.state.estadoCivil.value}
                    id="estadoCivil" 
                    onChange={this.handleChange}
                    >


                        <option value="solteiro">Solteiro(a)</option>
                        <option value="casado">Casado(a)</option>
                        <option value="divorciado">Divorciado(a)</option>
                        <option value="viuvo">Vi√∫vo(a)</option>  


                    </select>


                    <div id="tipoDocumentoContainer">
                    <label htmlFor="tipoDocumento">Tipo de Documento:</label>
                    <select id="tipoDocumento">
                        <option value="rg">RG</option>
                        <option value="cpf">CPF</option>
                        <option value="passaporte">CNPJ</option>
                    </select>
                    </div>

                </form>
            </div>
        )
    }
}

export default Form;
