import React from "react";
import styled from 'styled-components';


const StyledSubmitButton = styled.button`
    background-color: #3498db;
    color: white;
    padding: 10px 20px;
    border: none;
    border-radius: 5px;
    cursor: pointer;
`;

const TextError = styled.p `
    color: red;
    font-size: 12px;
    `;

class Form extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            nome: {
                value: "",
                isError: false,
                isValid: false
            },
            idade: {
                value: "",
                isError: false,
                isValid: false
            },   
            cpf: {
                value: "cpf",
            },
            genero: {
                value: "",
            },
            estadoCivil: {
                value: "",
            },
            tipoDocumento: {
                value: "rg", // valor padrão para o tipo de documento
            },
            loadingVisible: false,
            formularioEnviado: false,
            dadosFormulariosEnviados: [], // Array para armazenar os dados de todos os formulários enviados


        }
    }


    handleChange = (event) => {
        const { name, value } = event.target;
        this.setState({
            [name]: {
                value,
            },
        });
    };

    handleChangeGender = (event) => {
        const { value } = event.target;
        this.setState({
            genero: value,
        });
    };
    
    
    


    handleSubmit = (event) => {
        event.preventDefault();
    
        // acessar os valores dos campos no estado do componente
        const { nome, idade, genero, estadoCivil, tipoDocumento } = this.state;
        const dadosFormulario = {
            nome: nome.value,
            idade: idade.value,
            genero: genero,
            estadoCivil: estadoCivil.value,
            tipoDocumento: tipoDocumento.value,
        };

        if (nome.value === "") {
            this.setState({
                nome: {
                    ...nome,
                    isError: true
                },
                idade: {
                    ...idade,
                    isError: true
                }
            });
            return; 
        }

        this.setState({ loadingVisible: true });
        setTimeout(() => {
            this.setState((prevState) => ({
                dadosFormulariosEnviados: [...prevState.dadosFormulariosEnviados, dadosFormulario],
    
                loadingVisible: false,
                formularioEnviado: true,
                dadosFormulario,
    
                nome: { value: "" },
                idade: { value: "" },
                genero: "", 
                estadoCivil: { value: "" },
                tipoDocumento: { value: "rg" },
            }));
            
    }, 2000);
    

    };
    
    
    render() {
        return(
            <div className="main">
                <form onSubmit={this.handleSubmit}>
                <h3>Dados</h3>
                    <label>Nome:</label>
                    <input type="text" value={this.state.nome.value} name="nome" 
                    onChange={this.handleChange}/>
                    {this.state.nome.isError &&
                    <TextError>O nome é obrigatório</TextError>}
                    {/* {this.state.nome.isError &&
                    <TextError>O nome está incompleto</TextError>} */}

                    <label>Idade:</label>
                    <input type="number" value={this.state.idade.value} name="idade"
                    onChange={this.handleChange}/>
                {this.state.idade.isError && <TextError>Este campo é requerido</TextError>}


                    <label>Genero:</label>   
                    
                        <div>
                            <input 
                            type="radio" 
                            name="genero"
                            value="Masculino" 
                            id="masculino" 
                            onChange={this.handleChangeGender}
                            />
                            <label>Masculino</label>
                        </div> 

                        <div>
                            <input 
                            type="radio" 
                            name="genero" 
                            value="feminino" 
                            id="feminino"
                            onChange={this.handleChangeGender}

                            />
                            <label>Feminino</label>
                        </div> 

                        <div>
                            <input 
                            type="radio" 
                            name="genero" 
                            value="outro" 
                            id="outro"
                            onChange={this.handleChangeGender}

                            />
                            <label>Outro</label>
                        </div> 


                    <label htmlFor="estadoCivil">Estado Civil:</label>

                    <select 
                    name="estadoCivil"
                    value={this.state.estadoCivil.value}
                    id="estadoCivil" 
                    onChange={this.handleChange}
                    >


                        <option>Selecione</option>
                        <option value="solteiro">Solteiro(a)</option>
                        <option value="casado">Casado(a)</option>
                        <option value="divorciado">Divorciado(a)</option>
                        <option value="viuvo">Viúvo(a)</option>  


                    </select>

            


                    <div>
                        
                        <label htmlFor="tipoDocumento">Tipo de Documento:</label>
                        
                        <select
                        value={this.state.tipoDocumento.value}
                        onChange={this.handleChange}
                        name="tipoDocumento" 
                        id="tipoDocumento">
                            <option value="rg">RG</option>
                            <option value="cpf">CPF</option>
                            <option value="passaporte">CNPJ</option>
                        </select>

                    </div>

                    <StyledSubmitButton type="submit">Enviar</StyledSubmitButton>


                </form>

                {this.state.loadingVisible && <div className="loading"></div>}

        {this.state.dadosFormulariosEnviados.length > 0 && (
            <table>
                <thead>
                    <h2>Lista de dados</h2>
                </thead>
                <tbody>
                {this.state.dadosFormulariosEnviados.map((formulario, index) => (
                    <ul key={`${formulario.nome}-${formulario.idade}`}                      
                    style={{display: 'flex', alignItems: 'center'}}>
                        <li style={{listStyle: 'none', padding: '0 8px'}}>{formulario.nome}</li>
                        <li style={{listStyle: 'none', padding: '0 8px'}}>{formulario.idade}</li>
                        <li style={{listStyle: 'none', padding: '0 8px'}}>{formulario.genero}</li>
                        <li style={{listStyle: 'none', padding: '0 8px'}}>{formulario.estadoCivil}</li>
                        <li style={{listStyle: 'none', padding: '0 8px'}}>{formulario.tipoDocumento}</li>
                        <button>Reset</button>
                    </ul>
                ))}
                </tbody>
            </table>
            )}
        </div>
        );
    }
}

export default Form;
