import React from "react";

class Form extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            nome: {
                value: "",
            },
            idade: {
                value: "",
            },
            genero: {
                value: "",
            },
            estadoCivil: {
                value: "",
            },
            tipoDocumento: {
                value: "rg", // valor padr√£o para o tipo de documento
            },
            loadingVisible: false,
            formularioEnviado: false,

        }
    }


    handleChange = (event) => {
        const { name, value } = event.target;
        this.setState({
            [name]: {
                value,
            },
        });
    };

    handleChangeGender = (event) => {
        const { name, value } = event.target;
        this.setState({
            genero: {
                value,
            },
        });
    };
    


    handleSubmit = (event) => {
        event.preventDefault();
    
        // acessar os valores dos campos no estado do componente
        const { nome, idade, genero, estadoCivil, tipoDocumento } = this.state;
        const dadosFormulario = {
            nome: nome.value,
            idade: idade.value,
            genero: genero,
            estadoCivil: estadoCivil.value,
            tipoDocumento: tipoDocumento.value,
        };

        this.setState({ loadingVisible: true });
        setTimeout(() => {
        this.setState({
            loadingVisible: false,
            formularioEnviado: true,
            dadosFormulario,

            nome: { value: "" },
            idade: { value: "" },
            genero: "",
            estadoCivil: { value: "" },
            tipoDocumento: { value: "rg" },
        });
    }, 2000);
    

    };
    
    

    render() {
        return(
            <div className="main">
                <form onSubmit={this.handleSubmit}>
                    <label>Nome:</label>
                    <input type="text" value={this.state.nome.value} name="nome" 
                    onChange={this.handleChange}/>

                    <label>Idade:</label>
                    <input type="number" value={this.state.idade.value} name="idade" 
                    onChange={this.handleChange}/>

                    <label>Genero:</label>   
                    
                        <div>
                            <input 
                            type="radio" 
                            name="genero"
                            value="Masculino" 
                            id="masculino" 
                            onChange={this.handleChangeGender}
                            />
                            <label>Masculino</label>
                        </div> 

                        <div>
                            <input 
                            type="radio" 
                            name="genero" 
                            value="feminino" 
                            id="feminino"
                            onChange={this.handleChangeGender}

                            />
                            <label>Feminino</label>
                        </div> 

                        <div>
                            <input 
                            type="radio" 
                            name="genero" 
                            value="outro" 
                            id="outro"
                            onChange={this.handleChangeGender}

                            />
                            <label>Outro</label>
                        </div> 


                    <label htmlFor="estadoCivil">Estado Civil:</label>

                    <select 
                    name="estadoCivil"
                    value={this.state.estadoCivil.value}
                    id="estadoCivil" 
                    onChange={this.handleChange}
                    >


                        <option value="solteiro">Solteiro(a)</option>
                        <option value="casado">Casado(a)</option>
                        <option value="divorciado">Divorciado(a)</option>
                        <option value="viuvo">Vi√∫vo(a)</option>  


                    </select>


                    <div>
                        
                        <label htmlFor="tipoDocumento">Tipo de Documento:</label>
                        
                        <select
                        value={this.state.tipoDocumento.value}
                        onChange={this.handleChange}
                        name="tipoDocumento" 
                        id="tipoDocumento">
                            <option value="rg">RG</option>
                            <option value="cpf">CPF</option>
                            <option value="passaporte">CNPJ</option>
                        </select>

                    </div>

                    <button type="submit">Enviar</button>

                </form>

                {this.state.loadingVisible && <div className="loading"></div>}

                {this.state.formularioEnviado && !this.state.loadingVisible && (
                <table>
                    <thead>
                        <tr>
                            <th>Nome</th>
                            <th>Idade</th>

                            <th>Genero</th>
                            <th>Estado civil</th>
                            <th>Documento</th>
                        </tr>
                    </thead>
                    <tbody> 
                        <tr>
                            <td>{dadosFormulario.nome}</td>
                            <td>{dadosFormulario.idade}</td>
                            <td>{dadosFormulario.genero}</td>
                            <td>{dadosFormulario.estadoCivil}</td>
                            <td>{dadosFormulario.tipoDocumento}</td>
                        </tr>
                    </tbody>
                </table>  
                )}

            </div>
        )
    }
}

export default Form;
